import React, { useEffect, useState } from 'react'

function Home() {
  // const [login, setLogin] = useState(false);
  const [data, setData] = useState({username:'', role:''});

  useEffect(() => {
    fetch("/lecturerLogin")
      .then((res) => res.json())
      .then((data) => {setData({username: data.username, role: data.role})});
  }, []);

  async function handleClick(){
    // fetch("/employees")
    // .then((res) => res.json())
    // .then((data) => {setHome(data)});
    fetch('/login', {method: 'POST'})
    .then((res) => console.log(res))
    
  }

  //homepage: './' for package.json

  return (
    <div>
      <form>
        <span className="bg-red-500">Username: </span>
        <input className='border-solid border-2 border-sky-500 rounded-lg' type="text" name="username" id="username" />
        <br />
        <span>Password: </span>
        <input className='border-solid border-2 border-sky-500' type="password" name="password" id="password" />
        <br />
        <button onClick={handleClick}>Submit</button>

        <p>UserName: {!data ? "Loading..." : data.username}</p>
        <p>Role: {!data ? "Loading..." : data.role}</p>
      </form>
    </div>
  )
}

export default Home
