async function handleClick(e){
  e.preventDefault();
  fetch("/login")
    .then((res) => res.json())
    .then((data) => {
        // console.log(data.role);

        data.role === 'Lecturer' ? setlecturerRole(true) : setlecturerRole(false);
        data.role === 'Lecturer' ? setLecturerData({username: data.username, role: data.role}) : lecturerData({username: '', role: ''})
        // console.log(lecturerData.username)
        // console.log(lecturerRole)
    });
}

  return (
    <div className="flex flex-[80]">
      <form>
        <span className="bg-red-500">Username: </span>
        <input
          className="border-solid border-2 border-sky-500 rounded-lg"
          type="text"
          name="username"
          id="username"
        />
        <br />
        <span>Password: </span>
        <input
          className="border-solid border-2 border-sky-500 rounded-lg"
          type="password"
          name="password"
          id="password"
        />
        <br />
        <button onClick={handleClick}>Submit</button>

        {/* <p>Student UserName: {!data ? "Loading..." : data.username}</p>
        <p>Role: {!data ? "Loading..." : data.role}</p> */}

        
        {lecturerData && 
            <div>
                <p>Welcome {!lecturerData ? "Loading..." : lecturerData.username}!</p>
                <p>Role: {!lecturerData ? "Loading..." : lecturerData.role}</p>
            </div>
        }
        {/* {studentData &&

        } */}
        {/* {lecturerRole && lecturerRole === 'True' ? <p>Role: {!lecturerData ? "Loading..." : lecturerData.role}</p> : ""} */}
      </form>
    </div>
  );
}

export default Content;
