//     .then((res) => res.json())
//     .then((data) => {console.log(data);
//         setData({username: data.username, role: data.role})});
// }, []);

async function handleSubmit(e){
  e.preventDefault();

  console.log(JSON.stringify({username}));


  fetch("/login", { 
    headers: {
        'Accept': 'application/json, text/plain, */*',
        'Content-Type': 'application/json'
    },
    method: 'POST',
    body: JSON.stringify({username})
  })
    .then((res) => res.json())
    .then((data) => {
        // console.log(data.role);
        data.role === 'Lecturer' ? setlecturerRole(true) : setStudentRole(true);
        data.role === 'Lecturer' ? setLecturerData({username: data.username, role: data.role}) : setStudentData({username: data.username, role: data.role})
        // console.log(lecturerData.username)
        // console.log(lecturerRole)
    });
}

  return (
    <div className="flex flex-[80]">
      
    </div>
  );
}

export default Content;
