// }

function handleChange(e){
  console.log('Radio ' + e.target.name + ' selected ' + e.target.value);
  console.log(e.target.value)
}

function Card({question, id}) {

  const [fsValue, setFsValue] = useState('');

  return (
      <div className="card mx-auto bg-[#FFFFFF] rounded-lg text-xl p-6 my-4 w-3/5">
        <div className="px-7">
          <p className="text-left">{question}</p>
        </div>
        
        <div className="overflow-x-auto overflow-y-hidden">
        {/* <form action="" onSubmit={handleSubmit} className="w-full" method="post" enctype="multipart/form-data" id="myForm1"> */}
          <fieldset id={`fs${id}`} onChange={handleChange} className="radioGroup flex flex-row justify-around mt-[20px] text-center bg-[#F8F8FB]">
            {/* <div className="radioGroup flex flex-row justify-around mt-[20px] text-center bg-[#F8F8FB]"> */}
              <RatingButton title="Very poor contribution" radioValue="1" id={id} />
              <RatingButton title="Poor contribution" radioValue="2" id={id} />
              <RatingButton title="Acceptable contribution" radioValue="3" id={id} />
              <RatingButton title="Good contribution" radioValue="4" id={id} />
              <RatingButton title="Excellent contribution" radioValue="5" id={id} />
            {/* </div> */}
            
          </fieldset>
        {/* </form> */}
        </div>
      </div>
  );
}

export default Card;
