import React, { useEffect, useState } from "react";

function Content() {
// const [login, setLogin] = useState(false);
// const [data, setData] = useState({username:'', role:''});
const [lecturerData, setLecturerData] = useState(null);
const [lecturerRole, setlecturerRole] = useState(false);
const [studentData, setStudentData] = useState(null);
const [studentRole, setStudentRole] = useState(false);
const [username, setUsername] = useState('');
// useEffect(() => {
//   fetch("/studentLogin")
//     .then((res) => res.json())
//     .then((data) => {console.log(data);
//         setData({username: data.username, role: data.role})});
// }, []);

async function handleSubmit(e){
  e.preventDefault();

  console.log(username);

  fetch("/login"
  , 
  { 
      method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: username,
  }
  )
    .then((res) => res.json())
    .then((data) => {
        // console.log(data.role);
        data.role === 'Lecturer' ? setlecturerRole(true) : setStudentRole(true);
        data.role === 'Lecturer' ? setLecturerData({username: data.username, role: data.role}) : setStudentData({username: data.username, role: data.role})
        // console.log(lecturerData.username)
        // console.log(lecturerRole)
    });
}

  return (
    <div className="flex flex-[80]">
      <form onSubmit={handleSubmit}>
        <span className="bg-red-500">Username: </span>
        <input
          className="border-solid border-2 border-sky-500 rounded-lg"
          type="text"
          name="username"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <br />
        <span>Password: </span>
        <input
          className="border-solid border-2 border-sky-500 rounded-lg"
          type="password"
          name="password"
          id="password"
        />
        <br />
        <button type="submit">Submit</button>

        {/* <p>Student UserName: {!data ? "Loading..." : data.username}</p>
        <p>Role: {!data ? "Loading..." : data.role}</p> */}

        
        {lecturerData && 
            <div>
                <p>Welcome {!lecturerData ? "Loading..." : lecturerData.username}!</p>
                <p>Role: {!lecturerData ? "Loading..." : lecturerData.role}</p>
            </div>
        }
        {studentData &&
            <div>
                <p>Welcome {!studentData ? "Loading..." : studentData.username}!</p>
                <p>Role: {!studentData ? "Loading..." : studentData.role}</p>
            </div>
        }
      </form>
    </div>
  );
}

export default Content;
