import React, { useEffect, useState } from "react";

function Content() {
// const [login, setLogin] = useState(false);
const [data, setData] = useState({username:'', role:''});
const [lecturerData, setLecturerData] = useState({username:'', role:''});

useEffect(() => {
  fetch("/studentLogin")
    .then((res) => res.json())
    .then((data) => {console.log(data);
        setData({username: data.username, role: data.role})});
}, []);

async function handleClick(e){
  e.preventDefault();
  fetch("/lecturerLogin")
    .then((res) => res.json())
    .then((data) => {setData({username: data.username, role: data.role})});
}

  return (
    <div className="flex flex-[80]">
      <form>
        <span className="bg-red-500">Username: </span>
        <input
          className="border-solid border-2 border-sky-500 rounded-lg"
          type="text"
          name="username"
          id="username"
        />
        <br />
        <span>Password: </span>
        <input
          className="border-solid border-2 border-sky-500 rounded-lg"
          type="password"
          name="password"
          id="password"
        />
        <br />
        <button onClick={handleClick}>Submit</button>

        <p>UserName: {!data ? "Loading..." : data.username}</p>
        <p>Role: {!data ? "Loading..." : data.role}</p>
      </form>
    </div>
  );
}

export default Content;
