import "./App.css";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Home from "./Components/Home";
import SpeOne from "./Components/Pages/SpeOne";
import NavBar from "./Components/NavBar/NavBar";
import SpeTwo from "./Components/Pages/SpeTwo";
import Login from "./Components/Login";
import { useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { login, logout, selectUser } from "./features/userSlice"
import { auth } from './firebase';
import toast, { Toaster } from 'react-hot-toast';

function App() {
  const user = useSelector(selectUser);
  const dispatch = useDispatch();

  const welcomeUser = (userName, toastHandler = toast) => {
    toastHandler.success(
      `Welcome,  ${userName}!`,
    {
      icon: 'ðŸ‘‹',
      style: {
        borderRadius: '10px',
        background: '#333',
        color: '#fff',
      }
    })
  }

  useEffect(() => {
    auth.onAuthStateChanged(userAuth => {
      console.log(userAuth);
      if(userAuth){
        dispatch(login({ 
          email: userAuth.email,
          uid: userAuth.uid,
          displayName: userAuth.displayName,
          photoUrl: userAuth.photoURL}))
      }
      else{
        dispatch(logout());
      }
    })
    
  }, [])
  

  return (
    // user.auth ?
    
    
    
    <div className="App">
      {!user ?

      (
      <Login />
    ) : (
      <div className="app_body flex flex-row font-['Montserrat'] bg-[#E6ECEF]">
        <BrowserRouter>
          <NavBar />

          <Routes>
            <Route exact path="/" element={<Home />} />
            <Route path="/spe1" element={<SpeOne />} />
            <Route path="/spe2" element={<SpeTwo />} />
          </Routes>
          
        </BrowserRouter>
        <Toaster position="bottom-center" reverse={false} />
      </div>
    )}
    </div> 
  );
}

export default App;
