import { createContext, useState } from "react";

const UserContext = createContext({ name: "", auth: false });

function UserProvider({ children }) {
  const [user, setUser] = useState({ name: "", auth: false, role: "",  });

  const login = (username, password, ) => {

    fetch("/login", {
        headers: {
          Accept: "application/json, text/plain, */*",
          "Content-Type": "application/json",
        },
        method: "POST",
        body: JSON.stringify({ username, password }),
      })
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
          data.role === "Lecturer" ? setlecturerRole(true) : setStudentRole(true);
          data.role === "Lecturer"
            ? setLecturerData({ username: data.username, role: data.role, user: data.user })
            : setStudentData({ username: data.username, role: data.role, user: data.user });
        });

    setUser((user) => ({
      name: name,
      auth: true,
    }));
  };

  const logout = () => {
    setUser((user) => ({
      name: "",
      auth: false,
    }));
  };

  return (
    <UserContext.Provider value={{ user, login, logout }}>
      {children}
    </UserContext.Provider>
  );
}

export { UserProvider, UserContext };
