import "./App.css";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Home from "./Components/Home";
import SpeOne from "./Components/Pages/SpeOne";
import NavBar from "./Components/NavBar/NavBar";
import SpeTwo from "./Components/Pages/SpeTwo";
import Login from "./Components/Login";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login, logout, selectUser } from "./features/userSlice";
import { auth, db } from "./firebase";
import toast, { Toaster } from "react-hot-toast";

function App() {
  const user = useSelector(selectUser);
  const dispatch = useDispatch();
  const [userDetails, setUserDetails] = useState({
    name: "",
    email: "",
    role: "",
  });

  const welcomeUser = (userName, toastHandler = toast) => {
    toastHandler.success(`Welcome,  ${userName}!`, {
      icon: "ðŸ‘‹",
      style: {
        borderRadius: "10px",
        background: "#333",
        color: "#fff",
      },
    });
  };

  useEffect(() => {
    auth.onAuthStateChanged((userAuth) => {
      if (userAuth) {
        dispatch(
          login({
            email: userAuth.email,
            uid: userAuth.uid,
            displayName: userAuth.displayName,
            photoUrl: userAuth.photoURL,
          })
        );

        if (user.id) {
          db.collection("users")
            .doc(user.uid)
            .get()
            .then((doc) => {
              if (doc.exists) {
                const emailData = doc.data().email;
                const nameData = doc.data().name;
                const roleData = doc.data().role;

                setUserDetails({
                  name: nameData,
                  email: emailData,
                  role: roleData,
                });
                return nameData;
              }
            })
            .then((nameData) => {
              welcomeUser(nameData);
            });
        }
      } else {
        dispatch(logout());
      }
    });
  }, []);

  // useEffect(() => {
  //   if(!user.id) return
  //   try{
  //     db.collection("users").doc(user.uid).get()
  //     .then((doc) => {
  //       if(doc.exists){
  //         console.log(doc.data().email)
  //         const emailData = doc.data().email;
  //         const nameData = doc.data().name;
  //         const roleData = doc.data().role;

  //         setUserDetails({name: nameData, email: emailData, role: roleData})
  //         return nameData
  //       }
  //     }).then((nameData) => {
  //       console.log(nameData + " received.");
  //       welcomeUser(nameData)})
  //   }
  //   catch(err){
  //     console.log(err)
  //   }
  // }, [])

  return (
    <div className="App">
      {!user ? (
        <Login />
      ) : (
        <div className="app_body flex flex-row font-['Montserrat'] bg-[#E6ECEF]">
          <BrowserRouter>
            <NavBar />

            <Routes>
              <Route exact path="/" element={<Home />} />
              <Route path="/spe1" element={<SpeOne />} />
              <Route path="/spe2" element={<SpeTwo />} />
            </Routes>
          </BrowserRouter>
          <Toaster position="bottom-center" reverse={false} />
        </div>
      )}
    </div>
  );
}

export default App;
