import React, { useEffect, useState } from 'react'

function Home() {
  const [login, setLogin] = useState(false);
  const [data, setData] = useState({username:'', role:''});

  useEffect(() => {
    fetch("/lecturerLogin")
      .then((res) => res.json())
      .then((data) => {setData({username: data.username, role: data.role})});
  }, []);

  // async function handleClick(){
  //   // fetch("/employees")
