import React, { useState } from "react";
// import { UserContext } from "../context/UserContext";
import { login } from "../features/userSlice";
import { useDispatch } from "react-redux";
import { auth, db } from "../firebase";
// import Modal from "./Modal";
import LoginForm from "./LoginPageForms/LoginForm";
import RegisterForm from "./LoginPageForms/RegisterForm";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [profilePic, setProfilePic] = useState("");
  const [isRegistered, setIsRegistered] = useState(true);
  // const [modal, setModal] = useState(false);
  const dispatch = useDispatch();

  function handleClick() {
    setIsRegistered((currState) => !currState);
  }

  function handleRegister(){
    auth.createUserWithEmailAndPassword(email, password)
      .then((cred) => {
        db.collection('users').doc(cred.user.uid)
            db.collection('users').doc(cred.user.uid).set({
              name: name,
              email: email,
              profilePic: profilePic,      
          }, (err) => console.log(err));
      })
  }

  async function handleSignIn(e) {
    e.preventDefault();
    // console.log("Entered fields: " + JSON.stringify({ username, password }));
    auth.signInWithEmailAndPassword(email, password)
      .then((userAuth) => {
        dispatch(
          login({
            email: userAuth.user.email,
            uid: userAuth.user.uid,
            displayName: userAuth.user.displayName,
            photoUrl: userAuth.user.photoURL,
          })
        );
      })
      .catch((error) => alert(error));
  }
  return (
    <div className="">
      {/* {modal && <Modal setModal={setModal} />} */}
      <div className="bg-white ">
        <div className="flex justify-center h-screen">
          <div
            className="hidden bg-cover lg:block lg:w-2/3"
            style={{
              backgroundImage:
                "url(https://images.unsplash.com/photo-1616763355603-9755a640a287?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80)",
            }}
          >
            <div className="flex items-center h-full px-20 bg-gray-900 bg-opacity-40">
              <div>
                <h2 className="text-4xl font-bold text-white">
                  Self And Peer Evaluation Portal
                </h2>

                <p className="max-w-xl mt-3 text-gray-300">
                  This portal allows students to do self and peer evaluation and
                  submit to the related units they are currently attending.
                </p>
              </div>
            </div>
          </div>

          <div className="flex items-center w-full max-w-md px-6 mx-auto lg:w-2/6">
            {isRegistered ? (
              <LoginForm
                handleSignIn={handleSignIn}
                handleClick={handleClick}
                setEmail={setEmail}
                setPassword={setPassword}
                email={email}
                password={password}
              />
            ) : (
              <RegisterForm
                handleRegister={handleRegister}
                handleClick={handleClick}
                setEmail={setEmail}
                setPassword={setPassword}
                email={email}
                password={password}
                name={name}
                setName={setName}
                profilePic={profilePic}
                setProfilePic={setProfilePic}
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
